import { Component, OnInit } from '@angular/core';
import { CandidateService } from 'src/app/services/candidate.service'; 
import { PresidentCandidate } from 'src/models/PresidentCandidate';
import { Gender } from 'src/Enums/Gender';
import { Party } from 'src/Enums/Party';
import { State } from 'src/Enums/State';

@Component({
  selector: 'app-candidate',
  templateUrl: './candidate.component.html',
  styleUrls: ['./candidate.component.scss']
})
export class CandidateComponent {
  candidates: PresidentCandidate[] = [];

  constructor(private candidateService: CandidateService) { }

  getAllCandidates() {
    this.candidateService.getAllCandidates().subscribe(candidates => {
      this.candidates = candidates;
    });
  }

  registerCandidate(candidate: PresidentCandidate) {
    this.candidateService.registerCandidate(candidate).subscribe(createdCandidate => {
      console.log('Candidate registered successfully:', createdCandidate);
    });
  }

  getCandidateByNationalId(nationalId: string) {
    this.candidateService.getCandidateByNationalId(nationalId).subscribe(candidate => {
      console.log('Candidate retrieved successfully:', candidate);
    });
  }

  updateCandidate(nationalId: string, candidate: PresidentCandidate) {
    this.candidateService.updateCandidate(nationalId, candidate).subscribe(result => {
      console.log('Candidate updated successfully:', result);
    });
  }

  deleteCandidate(nationalId: string) {
    this.candidateService.deleteCandidate(nationalId).subscribe(result => {
      console.log('Candidate deleted successfully:', result);
    });
  }
}
